$("document").ready((function(){let e="",n=0;const t=$(window).innerWidth();n=t>=1200?150:t>=992?120:t>=768?90:t>=600?70:30;for(let t=0;t<n;t++)e+='<div class="bubble" style="--size:'+(2+4*Math.random())+"rem; --distance:"+(6+4*Math.random())+"rem; --position:"+(110*Math.random()-5)+"%; --time:"+(2+2*Math.random())+"s; --delay:"+-1*(2+2*Math.random())+'s;"></div>';$(".bubbles").prepend(e)}));